		function imgSize(imgvar) {
			var imgvarx = -45;
			var imgvary = -38;
			imgvar.x = imgvarx;
			imgvar.y = imgvary;
		
			var originalWidth = imgvar.getBounds().width;
			var originalHeight = imgvar.getBounds().height;
			imgvar.scaleX = imgwidth / originalWidth;
			imgvar.scaleY = imgheight / originalHeight;
		
			var maskShape = new createjs.Shape(new createjs.Graphics().f("#000").drawRoundRect(imgvarx, imgvary, imgwidth, imgheight, 15));
			imgvar.mask = maskShape;
		}
		
