		function up(evt) {
			var mesiahIndex = null;
			for (i = 0; i < userAnswers.length; i++) {
				if (getText(evt.currentTarget) == getText(userAnswers[i])) {
					mesiahIndex = i;
					userAnswers[i] = null;
				}
			}
		
			for (i = 0; i < userAnswers.length; i++) {
				var checkAnswer = stage.getChildByName("placeholder" + i);
				if (intersect(evt.currentTarget, checkAnswer)) {
					if (userAnswers[i] == null) {
						evt.currentTarget.x = checkAnswer.x;
						evt.currentTarget.y = checkAnswer.y;
						userAnswers[i] = evt.currentTarget;
						stage.stage.getChildByName("placeholder" + i).gotoAndPlay(1);
					} else {
						replaceAnswer(evt.currentTarget, userAnswers[i], mesiahIndex, i);
					}
					createCheckButton();
					return;
				}
			}
			returnHome(evt.currentTarget);
			createCheckButton();
		}
		
